func TestAccOctopusDeployAzureSubscriptionAccountBasic(t *testing.T) {
	localName := acctest.RandStringFromCharSet(20, acctest.CharSetAlpha)
	resourceName := "octopusdeploy_azure_subscription_account." + localName

	azureEnvironment := "AzureChinaCloud"
	certificate := ``
	certificateThumbprint := "5F7E885545F7E55A999712B765D338349D8CED8C"
	description := acctest.RandStringFromCharSet(20, acctest.CharSetAlpha)
	name := acctest.RandStringFromCharSet(20, acctest.CharSetAlpha)
	subscriptionID := uuid.New()
	tenantedDeploymentMode := core.TenantedDeploymentModeTenantedOrUntenanted

	newDescription := acctest.RandStringFromCharSet(20, acctest.CharSetAlpha)

	resource.Test(t, resource.TestCase{
		CheckDestroy:             testAccountCheckDestroy,
		PreCheck:                 func() { testAccPreCheck(t) },
		ProtoV6ProviderFactories: ProtoV6ProviderFactories(),
		Steps: []resource.TestStep{
			{
				Check: resource.ComposeTestCheckFunc(
					testAccountExists(resourceName),
					resource.TestCheckResourceAttr(resourceName, "azure_environment", azureEnvironment),
					// resource.TestCheckResourceAttr(resourceName, "certificate", certificate),
					// resource.TestCheckResourceAttr(resourceName, "certificate_thumbprint", certificateThumbprint),
					resource.TestCheckResourceAttr(resourceName, "description", description),
					resource.TestCheckResourceAttr(resourceName, "name", name),
					resource.TestCheckResourceAttr(resourceName, "subscription_id", subscriptionID.String()),
					resource.TestCheckResourceAttr(resourceName, "tenanted_deployment_participation", string(tenantedDeploymentMode)),
				),
				Config: testAzureSubscriptionAccountBasic(localName, azureEnvironment, certificate, certificateThumbprint, name, description, subscriptionID, tenantedDeploymentMode),
			},
			{
				Check: resource.ComposeTestCheckFunc(
					testAccountExists(resourceName),
					resource.TestCheckResourceAttr(resourceName, "azure_environment", azureEnvironment),
					// resource.TestCheckResourceAttr(resourceName, "certificate", certificate),
					// resource.TestCheckResourceAttr(resourceName, "certificate_thumbprint", certificateThumbprint),
					resource.TestCheckResourceAttr(resourceName, "description", newDescription),
					resource.TestCheckResourceAttr(resourceName, "name", name),
					resource.TestCheckResourceAttr(resourceName, "subscription_id", subscriptionID.String()),
					resource.TestCheckResourceAttr(resourceName, "tenanted_deployment_participation", string(tenantedDeploymentMode)),
				),
				Config: testAzureSubscriptionAccountBasic(localName, azureEnvironment, certificate, certificateThumbprint, name, newDescription, subscriptionID, tenantedDeploymentMode),
			},
		},
	})
}

func testAzureSubscriptionAccountBasic(localName string, azureEnvironment string, certificate string, certificateThumbprint string, name string, description string, subscriptionID uuid.UUID, tenantedDeploymentParticipation core.TenantedDeploymentMode) string {
	return fmt.Sprintf(`resource "octopusdeploy_azure_subscription_account" "%s" {
		azure_environment = "%s"
		description = "%s"
		name = "%s"
		management_endpoint = "https://management.core.chinacloudapi.cn/"
		storage_endpoint_suffix = "core.chinacloudapi.cn"
		subscription_id = "%s"
		tenanted_deployment_participation = "%s"
	}`, localName, azureEnvironment, description, name, subscriptionID, tenantedDeploymentParticipation)
}

// TestAzureSubscriptionAccountResource verifies that an azure account can be reimported with the correct settings
func TestAzureSubscriptionAccountResource(t *testing.T) {
	// I could not figure out a combination of properties that made this resource work
	return

	testFramework := test.OctopusContainerTest{}

	newSpaceId, err := testFramework.Act(t, octoContainer, "../terraform", "8-azuresubscriptionaccount", []string{})

	if err != nil {
		t.Fatal(err.Error())
	}

	// Assert
	client, err := octoclient.CreateClient(octoContainer.URI, newSpaceId, test.ApiKey)
	query := accounts.AccountsQuery{
		PartialName: "Subscription",
		Skip:        0,
		Take:        1,
	}

	resources, err := client.Accounts.Get(query)
	if err != nil {
		t.Fatal(err.Error())
	}

	if len(resources.Items) == 0 {
		t.Fatalf("Space must have an account called \"Subscription\"")
	}
	resource := resources.Items[0].(*accounts.AzureSubscriptionAccount)

	if resource.AccountType != "AzureServicePrincipal" {
		t.Fatal("The account must be have a type of \"AzureServicePrincipal\"")
	}

	if resource.Description != "A test account" {
		t.Fatal("BUG: The account must be have a description of \"A test account\"")
	}

	if resource.TenantedDeploymentMode != "Untenanted" {
		t.Fatal("The account must be have a tenanted deployment participation of \"Untenanted\"")
	}

	if len(resource.TenantTags) != 0 {
		t.Fatal("The account must be have no tenant tags")
	}
}
